mapper.select:
SELECT A.ID AS POST_ID , A.STUDY_ID AS STUDY_ID , A.MEMBER_ID AS MEMBER_ID , C.NICKNAME AS MEMBER_NICKNAME , A.TITLE AS POST_TITLE , A.CONTENT AS POST_CONTENT , A.COMMENT_COUNT AS COMMENT_COUNT , A.CREATED_AT AS POST_CREATED_AT , A.UPDATED_AT AS POST_UPDATED_AT , A.VISIBILITY AS POST_VISIBILITY FROM STUDY_POST A JOIN STUDY_MEMBER B ON A.MEMBER_ID = B.ID JOIN MEMBER C ON B.MEMBER_ID = C.ID WHERE A.STUDY_ID = #{studyId} AND A.VISIBILITY = 'Y' AND B.ROLE != 'NOT_MEMBER' ORDER BY A.CREATED_AT DESC
LIMIT #{size}
OFFSET #{offset}

SELECT A.ID AS POST_ID , A.STUDY_ID AS STUDY_ID , A.MEMBER_ID AS MEMBER_ID , C.NICKNAME AS MEMBER_NICKNAME , A.TITLE AS POST_TITLE , A.CONTENT AS POST_CONTENT , A.COMMENT_COUNT AS COMMENT_COUNT , A.CREATED_AT AS POST_CREATED_AT , A.UPDATED_AT AS POST_UPDATED_AT , A.VISIBILITY AS POST_VISIBILITY FROM STUDY_POST A JOIN STUDY_MEMBER B ON A.MEMBER_ID = B.ID JOIN MEMBER C ON B.MEMBER_ID = C.ID WHERE A.VISIBILITY = 'N' ORDER BY A.CREATED_AT DESC
LIMIT #{size}
OFFSET #{offset}

SELECT A.ID AS COMMENT_ID , A.POST_ID AS POST_ID , A.MEMBER_ID AS MEMBER_ID , C.NICKNAME AS MEMBER_NICKNAME , A.PARENT_ID AS PARENT_ID , A.CONTENT AS COMMENT_CONTENT , A.CREATED_AT AS COMMENT_CREATED_AT , A.UPDATED_AT AS COMMENT_UPDATED_AT , A.VISIBILITY AS COMMENT_VISIBILITY FROM STUDY_COMMENT A JOIN STUDY_MEMBER B ON A.MEMBER_ID = B.ID JOIN MEMBER C ON B.MEMBER_ID = C.ID WHERE A.VISIBILITY = 'N' ORDER BY A.CREATED_AT DESC
LIMIT #{size}
OFFSET #{offset}
