mapper.select:
SELECT A.ID AS COMMENT_ID , A.POST_ID AS POST_ID , A.PARENT_ID AS PARENT_ID , A.CONTENT AS COMMENT_CONTENT , B.NICKNAME AS MEMBER_NICKNAME , C.NAME AS RANK_NAME , A.CREATED_AT AS CREATED_AT , A.UPDATED_AT AS UPDATED_AT FROM STUDY_RECRUIT_COMMENT A JOIN MEMBER B ON A.MEMBER_ID = B.ID JOIN MEMBER_RANK C ON B.RANK_ID = C.ID AND A.VISIBILITY = 'N' ORDER BY A.ID DESC
LIMIT #{size}
OFFSET #{offset}
